import "./Books.css"
import {useParams, useNavigate} from "react-router-dom"
import React, { useContext, useEffect, useState } from "react"
import { BooksContext } from "./BookDataProvider"





export const BookCard = ({book})=> {

    const handleDetails = () => {

        const {getBooksById, deleteBook} = useContext(BooksContext)
        const [book, setBooks] = useState({})
        const {BookId} = useParams();	
    
        const navigate = useNavigate();
        
        useEffect(() => {
            console.log("useEffect", BookId)
            getBooksById(BookId)
            .then((response) => {
            setBooks(response)
            })
            }, [])
    
    
    }
 
    return(
    <section className="book">
        
        <h2>{ book.title }</h2>
        <div className="book__author"><h4>{book.author}</h4></div>
        <div className="book__genre"><i>genre: </i>{book.genre}</div>
        <div className="book__signed"><i>signed: </i>{book.signed ? "SIGNED!!!" : "not signed"}</div>
        <div className="book__read"><i>read: </i>{book.read ? "READ!" : "to be read"}</div>
        <div className="book__owned"><i>owned: </i>{book.owned ? "OWN IT!" : "not owned"}</div>
        {/* <button className="btn btn-primary"><Link to={`/books/detail/${book.id}`}> Details </Link></button> */}
        <button className="btn btn-primary" onClick={() => {handleDetails(`/books/details/${book.id}`)}}>Details</button>
        
    </section>
    )
}
